import React from "react";
import { Link } from "react-router-dom";
//import image
//import ItemImg from "../assets/img/card-item-3.jpg";
//import data
import Data from "../data";

function CardWebdev() {
    const Webdev = Data.filter(item => 
        item.category === 'webdesign');
    // Transform the elements of the array
    let mappedArray = Webdev.map(x => x * 2);
    console.log(mappedArray);

    // Shuffle the array
    //mappedArray.sort(() => Math.random() - 0.5);

    // Select the first 10 elements
   //let selectedElements = mappedArray.slice(0, 10);
    //console.log(selectedElements);
        //console.log(Webdev);
  return (
    <div>
      {Webdev.map((item, index) => {

        return (
          <Link to={`/explore/${item.id}`} key={index}>
            <article>
              <div className="col-md-4">
                <div className="post">
                  <div className="post-img">
                    <img src={item.image} className="rounded-lg" alt="img" />
                  </div>
                  <div className="post-body">
                    <div className="post-meta">
                      <div className="post-category cat-1 w-fit">
                        {item.type}
                      </div>
                      <span className="post-date">Price: $ {item.price}</span>
                      <span className="post-date ml-10"> {item.date}</span>
                    </div>
                    <h3 className="post-title">
                      <div >" {item.title} "</div>
                    </h3>

                      <span className="post-date font-normal">Author: {item.author}</span>
                  </div>
                </div>
              </div>
            </article>
          </Link>
        );
      })}
    </div>
  );
}

export default CardWebdev;
