import React from "react";
// import data
import Data from "../data";
// import use params
import { useParams } from "react-router-dom";
//import Link
//import {Link} from 'react-router-dom';

// get the  house id
//console.log(id);
//get the house based on id
//console.log(house);

//import style
const sizeStyle = {
  height: "auto",
  width: "20rem",
};

const ItemsDetails = () => {
  const { id } = useParams();
  const item = Data.find((item) => {
    // get the  house id
    return item.id === parseInt(id);
  });
  //console.log(item);
  return (
    <div className="">
      <TopBar />
    <div className="DetailedItem">
      <div className="d-flex flex-wrap justify-content-around p-4">
        <div className="">
          <img src={item.image} alt="" style={sizeStyle} className="rounded" />
        </div>
        <div className="flex flex-col ">
          <div className="">
            <div className="fw-bolder fs-1 p-3">" {item.name} "</div>
            <div className="flex items-center justify-center">
              <p>Description : {item.description}</p>
            </div>
            <div className="p-3 flex justify-content-center ">
              <div>
                <button className="d-flex flex-row border border-3 rounded p-3 justify-content-between fs-3 fw-semibold transition">
                  <i className="ri-download-cloud-2-line"></i>
                  <h2 className="pl-2">Download</h2>
                </button>
              </div>
            </div>
          </div>
          <div className="p-3"></div>
        </div>
      </div>
    </div>
    </div>
  );
};

export default ItemsDetails;
