import React from "react";
// import data
import Data from "../data";
// import use params
import { useParams } from "react-router-dom";
//import Link
import {Link} from 'react-router-dom';
//import icons
import { BiDownload, BiStar, BiBed, BiBath, BiArea } from "react-icons/bi";
// get the  house id
//console.log(id);
//get the house based on id
//console.log(house);

//import style
/* const sizeStyle = {
  height: "auto",
  width: "20rem",
}; */

const ItemsDetails = () => {
  const { id } = useParams();
  const item = Data.find((item) => {
    // get the  house id
    return item.id === parseInt(id);
  });
  //console.log(item);
  return (
    <section>
      <div className="container mx-auto min-h-[800px] mb-14">
        <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between">
          <div className="">
            <h2 className="text-2xl font-semibold">{item.name}</h2>
            <h3 className="text-lg mb-4">{item.description}</h3>
          </div>
          <div className="mb-4 lg:mb-0 flex gap-x-2 text-sm">
            <div className="bg-green-500 text-white px-3 rounded-full">
              {item.type}
            </div>
            <div className="bg-violet-500 text-white px-3 rounded-full">
              {item.name}
            </div>
          </div>
          <div className="text-3xl font-semibold text-violet-600">
            $ {item.price}
          </div>
        </div>
        <div className="flex flex-col items-start gap-8 lg:flex-row">
          <div className="max-w-[768px]">
            <div className="mb-8">
              <img src={item.image} alt="" />
            </div>
            <div className="flex gap-x-6 text-violet-700 mb-6">
              <div className="flex gap-x-2 items-center">
                <BiBed className="text-2xl"/>
                  <div>{item.type}</div>
              </div>
              <div className="flex gap-x-2 items-center">
                <BiBath className="text-2xl"/>
                  <div>{item.type}</div>

              </div>
              <div className="flex gap-x-2 items-center">
                <BiArea className="text-2xl"/>
                  <div>{item.name}</div>
              </div>
            </div>
              <div className="">{item.description}</div>
          </div>
          <div className="flex-1 bg-white w-full mb-8 border border-gray-300 rounded-lg px-6 py-8">
            <div className="flex items-center gap-x-4 mb-8">


            </div>
            <div className="flex gap-x-2">
                <button className="bg-violet-700 hover:bg-violet-800 text-white rounded flex items-center justify-center p-4 text-sm w-6 transition"><BiDownload /></button>
                <button className="border border-violet-700 hover:border-violet-500 text-violet-700  hover:text-violet-500 rounded p-1 text-sm w-6 transition"><BiStar /></button>
              </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default ItemsDetails;
