import React from "react";
// import data
import Data from "../data";
// import use params
import { useParams } from "react-router-dom";
//import Link
import {Link} from 'react-router-dom';

// get the  house id
//console.log(id);
//get the house based on id
//console.log(house);

//import style
const sizeStyle = {
  height: "auto",
  width: "20rem",
};

const ItemsDetails = () => {
  const { id } = useParams();
  const item = Data.find((item) => {
    // get the  house id
    return item.id === parseInt(id);
  });
  //console.log(item);
  return (
    <div className="">
      <div className="TopBar">
            <div className="d-flex flex-column pb-5 justify-content-center">
                <div className="d-flex pb-3 justify-content-center">
                    <h1 className="fw-bold fs-3">Item: {item.name}</h1>
                </div>
                <div className="d-flex flex-row justify-content-center">
                    <Link to='/'>
                      <h3  className="pl-3 fs-6 fw-normal">Home </h3>
                      </Link>
                    <h3  className="pl-3 fs-6 fw-normal"> / </h3>
                    <Link to='/explore/'>
                      <h3  className="pl-3 fs-6 fw-normal">Explore </h3>
                      </Link>
                    <h3  className="pl-3 fs-6 fw-normal"> / </h3>
                    <h3  className="pl-3 fs-5 fw-semibold">item id: {item.id}</h3>
                </div>
            </div>
        </div>
    <div className="">
      <div className="flex flex-row items-center justify-between gap-8">
        <div className="">
          <img src={item.image} alt={item.name} style={sizeStyle} className="rounded" />
        </div>
        <div className="flex flex-col">
          <div className="">
            <div className=" p-3">" {item.name} "</div>
            <div className="flex flex-col ">
              <p>{item.description}</p>
              <p>Description : {item.largeDesc}</p>
            </div>
            <div className="p-3 flex justify-content-center ">
              <div>
                <a download={item.name} href={item.image} className="d-flex flex-row border border-3 rounded p-3 justify-content-between fs-3 fw-semibold hover:text-green-500 transition">
                  <i className="ri-download-cloud-2-line"></i>
                  <h2 className="pl-2">Download</h2>
                </a>
              </div>
            </div>
          </div>
          <div className="p-3"></div>
        </div>
      </div>
    </div>
    </div>
  );
};

export default ItemsDetails;
