import React from 'react';
import {Link} from 'react-router-dom';

//import data
import Data from '../data';

function PostRow() {
  // Transform the elements of the array
let mappedArray = Data.map(x => x * 2);
console.log(mappedArray);
// Shuffle the array
mappedArray.sort(() => Math.random() - 0.5);
// Select the first 10 elements
let selectedElements = mappedArray.slice(0, 10);
console.log(selectedElements);

  return (
    <>
    <div className='this overflow-y-scroll'>
    {Data.map((item, index) =>{
        return(
            <div className="col-md-12">
      <div className="post post-row">
        <Link to={`/explore/${item.id}`} className="post-img" key={index}>
          <img className=' rounded-lg' src={item.image} alt="img" />
        </Link>
        <div className="post-body">
          <div className="post-meta">
            <Link to={`/explore/${item.id}`} className="post-category cat-2" key={index}>
              {item.type}
            </Link>
            <spnan className="post-date">{item.date}</spnan>
          </div>
          <h3 className="post-title">
            <Link to={`/explore/${item.id}`} key={index}>
              {item.title}
            </Link>
          </h3>
          <span className="post-date font-normal">Author: {item.author}</span>
          <p>
            {item.description}
          </p>
        </div>
      </div>
    </div>
        );
    })}
    </div>
    </>

  );
}

export default PostRow
