import React from "react";
// import data
import Data from "../data";
// import use params
import { useParams } from "react-router-dom";
//import Link
import { Link, useMatch, useResolvedPath } from "react-router-dom";
//import JavaImg
//import JavaImg from '../assets/img/xpost-1.jpg.pagespeed.ic.2GXRfHYjOg.webp'

// get the  house id
//console.log(id);
//get the house based on id
//console.log(house);
//import AD
import Advertisement from "../assets/img/xad-1.jpg.pagespeed.ic.qQJhsdJdF0.webp";

//import PostRow
import PostRow from "../components/PostRow";

const ItemsDetails = () => {
  const { id } = useParams();
  const item = Data.find((item) => {
    // get the  house id
    return item.id === parseInt(id);
  });
  console.log(item);
  return (
    <>
      <header id="header">
        <div
          className="p-5 w-fit bg-slate-100 flex items-center justify-center"
          id="post-header"
        >
          <div className="background-img">
            <img src={item.image} className="rounded-xl" alt="" />
          </div>
          <div className="container">
            <div className="row">
              <div className="col-md-10  flex items-center justify-between">
                <h1>{item.title}</h1>
                <div className="post-meta flex items-center justify-center flex-col">
                  <div className="flex items-center justify-center">
                    <div className="w-fit post-category cat-2">{item.type}</div>
                    <div className="w-24 post-category cat-3">
                      $ {item.price}
                    </div>
                  </div>
                  <span className="post-date mt-4">{item.date}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>
      <div className="section">
        <div className="container">
          <div className="row">
            <div className="col-md-8">
              <div className="section-row sticky-container">
                <div className="main-post">
                  <h3>{item.title}</h3>
                  <h5 className="mb-6 flex items-center justify-center">
                    Post by
                    <span className=" bg-slate-800 text-slate-100 rounded-md ml-4 w-fit p-2 text-2xl font-bold">
                      @ {item.author}
                    </span>
                     <div className="ml-8">
                      {item.date}
                    </div>
                  </h5>
                  <p>
                    <div className="mb-8">
                      {item.description}
                    </div>
                    <figure className="figure-img iframe">
                      <iframe
                        src={item.iframeLink}
                        title="YouTube video player"
                        frameborder="0"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                        allowfullscreen
