import React from 'react';
import {Link} from 'react-router-dom';

//import data
import Data from '../data';

function PostRow() {
  return (
    <>
    {Data.map((item, index) =>{
        return(
            <div className="col-md-12">
      <div className="post post-row">
        <Link to={`/explore/${item.id}`} className="post-img" key={index}>
          <img className='w-16' src={item.image} alt="img" />
        </Link>
        <div className="post-body">
          <div className="post-meta">
            <Link to={`/explore/${item.id}`} className="post-category cat-2" key={index}>
              {item.type}
            </Link>
            <spnan className="post-date">March 27, 2022</spnan>
          </div>
          <h3 className="post-title">
            <Link to={`/explore/${item.id}`} key={index}>
              {item.name}
            </Link>
          </h3>
          <p>
            {item.description}
          </p>
        </div>
      </div>
    </div>
        );
    })}
    </>

  );
}

export default PostRow
