import React from "react";
// import data
import Data from "../data";
// import use params
import { useParams } from "react-router-dom";
//import Link
import { Link, useMatch, useResolvedPath } from "react-router-dom";
//import JavaImg
//import JavaImg from '../assets/img/xpost-1.jpg.pagespeed.ic.2GXRfHYjOg.webp'

// get the  house id
//console.log(id);
//get the house based on id
//console.log(house);
//import AD
import Advertisement from "../assets/img/xad-1.jpg.pagespeed.ic.qQJhsdJdF0.webp";

//import PostRow
import PostRow from '../components/PostRow';

const ItemsDetails = () => {
  const { id } = useParams();
  const item = Data.find((item) => {
    // get the  house id
    return item.id === parseInt(id);
  });
  console.log(item);
  return (
    <>
      <header id="header">
        <div className="p-5 w-fit bg-slate-100 flex items-center justify-center" id="post-header">
          <div className="background-img">
            <img src={item.image} className="rounded-xl"  alt=""/>
          </div>
          <div className="container">
            <div className="row">
              <div className="col-md-10  flex items-center justify-between">
                <h1>{item.name}</h1>
                <div className="w-fit border border-slate-800 p-4 rounded-lg">Download</div>
                <div className="post-meta">
                  <div className="flex items-center justify-center">
                  <div className="w-fit post-category cat-2">{item.type}</div>

                  </div>
                  <span className="post-date">March 23, 2022</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>
      <div className="section">
        <div className="container">
          <div className="row">
            <div className="col-md-8">
              <div className="section-row sticky-container">
                <div className="main-post">
                  <h3>{item.name}</h3>
                  <h5>
                    Post by <span>@{item.author}</span> - March 23, 2022
                  </h5>
                  <p>
                    {item.description}
                    <figure className="figure-img iframe">
                      <iframe
                        src="https://www.youtube.com/embed/vk_xq1P7vIU"
                        title="YouTube video player"
                        frameborder="0"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                        allowfullscreen
                        className="img-responsive"
                      />
                    </figure>
                    <blockquote className="blockquote">
                      I've heard the argument that "lorem ipsum" is effective in
                      wireframing or it helps people focus on the actual layout
                      Lorem ipsum dolor sit amet consectetur adipisicing elit.
                      Quod non odio animi. Voluptate rerum odio eius culpa
                      debitis illo possimus sunt.
                    </blockquote>
                    {item.largeDesc}
                  </p>
                </div>
                <div className="post-shares sticky-shares">
                  <a href="facebook.com" className="share-facebook">
                    <i class="ri-facebook-box-fill"></i>
                  </a>
                  <a href="twitter.com" className="share-twitter">
                    <i class="ri-twitter-fill"></i>
                  </a>
                  <a href="pinterest.com" className="share-pinterest">
                    <i class="ri-pinterest-fill"></i>
                  </a>
                  <a href="linkedin.com" className="share-linkedin">
                    <i class="ri-linkedin-box-fill"></i>
                  </a>
                  <a href="dribble.com" className="share-google-plus">
                    <i class="ri-dribbble-fill"></i>
                  </a>
                </div>
              </div>
            </div>

            <OtherSection />
          </div>
        </div>
      </div>

